PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE paths (  dev INTEGER NOT NULL, inode INTEGER NOT NULL , rdev INTEGER, mode INTEGER, nlink INTEGER, uid INTEGER, gid INTEGER, blksize INTEGER, blocks INTEGER, size INTEGER, atim REAL, atimn INTEGER, mtim REAL, mtimn INTEGER, ctim REAL, ctimn INTEGER, dirname TEXT, parentid INTEGER , last_updated REAL, inow REAL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f', 'NOW')) );
CREATE INDEX paths_dirname      ON paths (dirname);
CREATE UNIQUE INDEX paths_dev_uniq     ON paths (dev,inode);
CREATE UNIQUE INDEX paths_parent2_uniq ON paths (parentid,dirname);
CREATE INDEX paths_dev          ON paths (dev);
CREATE INDEX paths_inode        ON paths (inode);
CREATE INDEX paths_parentid     ON paths (parentid);
CREATE TRIGGER paths_lastupdated  AFTER UPDATE OF dev,inode,dirname,parentid  ON paths FOR EACH ROW BEGIN    UPDATE paths SET last_updated=DATETIME()  WHERE rowid=OLD.rowid ; END;
COMMIT;
